var AbstractModel = {
    _$: null,
    
    sections: {
        "pictures": {
            "main": function () {
                return "";
            }
            , 
            "other": function () {
                return [];
            }
        }, 
        "price": {
            "discount": function () {
                return "";
            }, 
            "regular": function () {
                return "";
            }, 
            "percent": function () {
                return "";
            }, 
            "benefit": function () {
                return "";
            }
        }, 
        "title": {
            "full": function () {
                return "";
            }, 
            "short": function () {
                return "";
            }
        }, 
        "seller": {
            "info": function () {
                return "";
            }
        },
        "description": {
            "full": function () {
                return "";
            }, 
            "short": function () {
                return "";
            }, 
            "map": function () {
                return "";
            }
        }
    },

    parse: function ($) {
        this._$ = $;
        
        return this._callRule({}, this.sections);
    },
    
    _callRule: function (obj, sections) {
        for (var section in sections) {
            if (sections.hasOwnProperty(section)) {
                    console.log(section);
                if (typeof sections[section] === 'object') {
                    obj[section] = {};
                    this._callRule(obj[section], sections[section]);
                }
                else if (typeof sections[section] === 'function') {
                    obj[section] = sections[section].call(this);
                }
            }
        }
        
        return obj;
    }
}