var template = {
    "pictures": {
        "main": function() {
            var result = []

            $('#main_deal_logo img').each(function() {
                result.push($(this).attr('src'))
            })

            return result
        } ()
        , "other": function () {
            var result = []

            $('#deal_body_blocks').find('.deal_features_wrapper p.rtecenter img').each(function() {
                result.push($(this).attr('src'))
            })

            return result
        } ()
    }
    , "price": {
        "discount": $('#deal_meta .price .price_integer').text().replace(/\s{2,}/g,' ').trim()
        , "regular": $('#deal_meta .info li').eq(0).children('span').eq(0).text().replace(/\s{2,}/g,' ').trim()
        , "percent": $('#deal_meta .info li').eq(1).children('big').text().replace(/\s{2,}/g,' ').trim()
        , "benefit": ''
    }
    , "title": {
        "full": $('#main_deal_logo > h1 > span').text().replace(/\s{2,}/g,' ').trim()
        , "short": ''
    }
    , "seller": {
        "info": $('#contacts_deal_box div.address').eq(1).html().replace(/\s{2,}/g,' ').trim()
    }
    , "description": {
        "full": $('#deal_body_blocks .deal_conditions_wrapper').html().replace(/\s{2,}/g,' ').trim()
        , "short": $('#deal_body_blocks .deal_features_wrapper').html().replace(/\s{2,}/g,' ').trim()
        , "map": ''
    }
}
