var Minuvalik = require("./models/www.minuvalik.ee"),
    fs = require('fs');

var pakkumised = (function() {
    var pakkumised = [];
    
    pakkumised.repositories = {
        'minuvalik.ee': {
            "model": Minuvalik,
            "urls": {
                'est': 'https://minuvalik.ee/ee/',
                'rus': 'https://minuvalik.ee/ru/'
            }
        }
    };

    pakkumised.parse = function($, site) {
        return this.pakkumised[site].model.parse($);
        
        // eval(fs.readFileSync(__dirname + '/models/' + site + '.js', 'utf8') + '')
        // return template
    }

    return pakkumised;
})();

module.exports = pakkumised;